import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { EmpleadosService } from '../empleados.service';
import { ServicioEmpleadosService } from '../servicio-empleados.service';

@Component({
  selector: 'app-proyectos',
  templateUrl: './proyectos.component.html',
  styleUrls: ['./proyectos.component.css']
})
export class ProyectosComponent implements OnInit{

  // le pasamos el routing e importamos el Router
  constructor(private router:Router, private miServicio:ServicioEmpleadosService, private empleadosService:EmpleadosService) {

  }

  ngOnInit(): void {

  }

  volverHome() {
    // accedemos el router del constructor y le pasamos navigate() y la ruta
    this.router.navigate(['']);
  }

  cuadroNombre:string = '';
  cuadroApellido:string = '';
  cuadroCargo:string = '';
  cuadroSalario:number = 0;

  agregarEmpleado() { // creamos una función que recoge la información de los empleados. Hay que agregar miEmpleado al array de arriba

    let miEmpleado = new Empleado(this.cuadroNombre, this.cuadroApellido, this.cuadroCargo, this.cuadroSalario);
    this.miServicio.muestraMensaje("Nombre del empleado: " + miEmpleado.nombre); // llamo al constructor, le pasamos la función del servicio
    this.empleados.push(miEmpleado); // añade a empleados lo que hay en miEmpleado
    this.empleadosService.agregarEmpleadoServicio(miEmpleado);
}
<p>proyectos works!</p>

<form class="form-group row mb-3">
    <div class="form-group col-md-3">
      <label for="nombre">Nombre:</label>
      <input type="text" class="form-control" name="nombre" id="nombre" placeholder="Ingrese su nombre" [(ngModel)]="cuadroNombre">
    </div>
    <div class="form-group col-md-3">
      <label for="nombre">Apelldio:</label>
      <input type="text" class="form-control" name="apellido" id="apellido" placeholder="Ingrese su apellido" [(ngModel)]="cuadroApellido">
    </div>
    <div class="form-group col-md-3">
      <label for="nombre">Cargo:</label>
      <input type="text" class="form-control" name="cargo" id="cargo" placeholder="Ingrese su cargo" [(ngModel)]="cuadroCargo">
    </div>
    <div class="form-group col-md-3">
      <label for="nombre">Salario:</label>
      <input type="text" class="form-control" name="salario" id="salario" placeholder="Ingrese su salario" [(ngModel)]="cuadroSalario">
    </div>
    <div class="col mt-2">
      <button type="submit" class="btn btn-primary" (click)="agregarEmpleado()">Agregar</button>
    </div>
  </form>

<div class="text-center">
    <!-- llama a la función al clickar para volver al home y la creamos en el component de proyectos-->
    <button class="btn btn-primary" (click)="volverHome()">Volver a home</button>
</div>
