import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Empleado } from "./empleado.model";

@Injectable()
export class DataServices{

    // injectamos el HttpClientModule desde el constructor

    constructor(private httpClient:HttpClient) {}

    // recibe como parámetro el array tipo Empleado creado en empleados.services
    guardarEmpleados(empleados:Empleado[]) {
        // utilizamos el objeto injectado
        this.httpClient.post('https://mis-clientes-6a9cd-default-rtdb.europe-west1.firebasedatabase.app/datos.json', empleados) // url base de datos (añadimos datos.json) y especifamos el array
    } 
}
