  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

  constructor() {}

  ngOnInit(): void {

  }

  login(form:NgForm) {

    const email = form.value.email;

    const password = form.value.password;
  }

}
