import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Empleado } from "./empleado.model";

@Injectable()
export class DataServices{

    // injectamos el HttpClientModule desde el constructor

    constructor(private httpClient:HttpClient) {}


    cargarEmpleados() {
       return this.httpClient.get('https://mis-clientes-6a9cd-default-rtdb.europe-west1.firebasedatabase.app/datos.json');
    }

    // recibe como parámetro el array tipo Empleado creado en empleados.services
    guardarEmpleados(empleados:Empleado[]) {
        // utilizamos el objeto injectado. si reemplazamos el post por put, sobreescribe los datos que ya están registrados anteriormente, evitando duplicados
        this.httpClient.put('https://mis-clientes-6a9cd-default-rtdb.europe-west1.firebasedatabase.app/datos.json', empleados).subscribe({ // url base de datos (añadimos datos.json) y especifamos el array
            next: response=>{console.log(response);},
            error: error=>{console.log(error)}
        });
    } 
}
